import React, { useEffect, useState, useCallback } from 'react';
import { NavLink as RouterLink } from 'react-router-dom';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import { THEMES } from 'src/constants';
import {
  Box,
  Button,
  Card,
  Container,
  Grid,
  Typography,
  useTheme,
  useMediaQuery,
  makeStyles
} from '@material-ui/core';
import useIsMountedRef from 'src/hooks/useIsMountedRef';
import { axios } from 'src/utils/axiosHook';
import { useDispatch, useSelector } from 'src/store';
import Carousel from 'react-elastic-carousel';

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.background.dark,
    paddingTop: 200,
    paddingBottom: 200,
    minHeight: '100%',
    [theme.breakpoints.down('md')]: {
      paddingTop: 60,
      paddingBottom: 60
    }
  },
  button: {
    color: theme.palette.text.secondary,
    padding: '10px 8px',
    justifyContent: 'flex-start',
    textTransform: 'none',
    letterSpacing: 0,
    width: '100%'
  },
  card: {
    width: '30%',
    height: '50%'
  }
}));

const items = [
  {id: 1, title: 'item #1'},
  {id: 2, title: 'item #2'},
  {id: 3, title: 'item #3'},
  {id: 4, title: 'item #4'},
  {id: 5, title: 'item #5'}
];

const Hero = ({ className, ...rest }) => {
  const classes = useStyles();
  const theme = useTheme();
  const mobileDevice = useMediaQuery(theme.breakpoints.down('sm'));

  return (
    <div
      className={clsx(classes.root, className)}
      {...rest}
    >
      {mobileDevice ?
        <Container maxWidth="lg">
          <Grid
            container
            spacing={3}
          >
            <Carousel itemsToShow={2}>
              {items.map(item => <div key={item.id}>{item.title}</div>)}
            </Carousel>
          </Grid>
        </Container>
      : <Container maxWidth="lg">
          <Grid
            container
            spacing={3}
          >
            <Carousel itemsToShow={2}>
              <div>
                <img src="static/images/panda.png" />
              </div>
              <div>
                <img src="static/chaingames.png" />
              </div>
              <div>
                <img src="static/images/apex.jpg" />
              </div>
              <div>
                <img src="static/images/fallGuys.jpg" />
              </div>
              <div>
                <img src="static/images/fifa21.jpg" />
              </div>
              <div>
                <img src="static/images/fortnite.png" />
              </div>
              <div>
                <img src="static/images/madden.jpg" />
              </div>
              <div>
                <img src="static/images/nba.png" />
              </div>
              <div>
                <img src="static/images/superCryptoKart.jpg" />
              </div>
            </Carousel>
          </Grid>
        </Container>
      }
    </div>
  );
};

Hero.propTypes = {
  className: PropTypes.string
};

export default Hero;
