import Carousel from "./Carousel/Carousel"

import { 
    Card,
    Grid,
    CardMedia,
    Typography,
    makeStyles
 } from '@material-ui/core';

const items = [
    {
        Name: "APEX",
        Image: "/static/images/apex.jpg"
    },
    {
        Name: "FIFA21",
        Image: "/static/images/fifa21.jpg"
    },
    {
        Name: "MADDEN21",
        Image: "/static/images/madden.jpg"
    },
    {
        Name: "APEX1",
        Image: "/static/images/apex.jpg"
    },
    {
        Name: "FIFA211",
        Image: "/static/images/fifa21.jpg"
    },
    {
        Name: "MADDEN211",
        Image: "/static/images/madden.jpg"
    }
]

const useStyles = makeStyles((theme) => ({
    card: {
      width: '100%',
      height: 320,
      padding: 10
    },
    image: {
      height: 320,
      backgroundColor: theme.palette.background.dark
    },
    title: {
      position: 'relative',
      '&:before': {
        position: 'absolute',
        bottom: -2,
        left: -16,
        content: '" "',
        height: 62,
        width: 6,
        backgroundColor: theme.palette.secondary.main,
        marginRight: '20px'
      }
    },
    media: {
        margin: 1,
        height: '100%',
        overflow: 'hidden',
        position: 'relative',
        transition: '300ms',
        cursor: 'pointer',
        '&:hover':
        {
            filter: 'brightness(115%)'
        }
    },
    mediaCaption: {
        textOverflow: 'ellipsis',

        position: 'absolute',
        bottom: 0,

        paddingLeft: '15px',
        paddingBottom: '10px',
        paddingTop: '10px',

        backgroundColor: 'black',
        color: 'white',
        opacity: 0.4,

        width: '100%',
        height: '15%',

        transition: '300ms',
        cursor: 'pointer',
        '&:hover': {
          opacity: 0.8
        }
    },
    banner: {
        height: 320
    }
  }));

const Banner = () => {
    const classes = useStyles();

    return (
        <Card raised className={classes.card}>
            <Carousel
                className={classes.banner}
                show={3}
                infiniteLoop
            >
                {
                items.map((item) => {
                    return(
                        <CardMedia
                            className={classes.media}
                            image={item.Image}
                            title={item.Name}
                        >
                            <Typography className={classes.mediaCaption}>
                                {item.Name}
                            </Typography>
                        </CardMedia>
                    )
                })
                }
            </Carousel>
        </Card>
    )
} 

export default Banner
