/* eslint-disable no-use-before-define */
import React, { useState, useEffect, useCallback } from 'react';
import { useLocation, matchPath } from 'react-router-dom';
import { Link as RouterLink } from 'react-router-dom';
import PerfectScrollbar from 'react-perfect-scrollbar';
import PropTypes from 'prop-types';
import {
  Avatar,
  Box,
  Button,
  Card,
  CardContent,
  Divider,
  Drawer,
  Hidden,
  Link,
  List,
  ListSubheader,
  Paper,
  Typography,
  makeStyles
} from '@material-ui/core';
import {
  User as UserIcon,
  Settings as ToolIcon,
  LogOut as LogoutIcon,
  List as ListIcon,
  CreditCard as CreditcardIcon,
  Phone as PhoneIcon,
  ChevronsDown as WithdrawIcon,
  Award as AwardIcon,
  Calendar as CalendarIcon
} from 'react-feather';
import Logo from 'src/components/Logo';
import NavItem  from './NavItem';
import NumberFormat from 'react-number-format';
import { useSelector } from 'src/store';


const mySections = [
  {
    items: [
      {
        title: 'My Events',
        icon: CalendarIcon,
        href: '/app/'
      },
      {
        title: 'My Challanges',
        icon: AwardIcon,
        href: '/app/'
      }
    ]
  }
];


const sections = [
  {
    items: [
      {
        title: 'Profile',
        icon: UserIcon,
        href: '/app/'
      },
      {
        title: 'My Wallet',
        icon: CreditcardIcon,
        href: '/app/'
      },
      {
        title: 'Transaction History',
        icon: ListIcon,
        href: '/app/'
      },
      {
        title: 'Withdraw',
        icon: WithdrawIcon,
        href: '/app/'
      },
      {
        title: 'Report Issue',
        icon: PhoneIcon,
        href: '/app/'
      },
      {
        title: 'Account Settings',
        icon: ToolIcon,
        href: '/app/'
      },
      {
        title: 'Logout',
        icon: LogoutIcon,
        href: '/app/'
      },
    ]
  }
];

function renderNavItems({
  items,
  pathname,
  depth = 0
}) {
  return (
    <List disablePadding>
      {items.reduce(
        (acc, item) => reduceChildRoutes({ acc, item, pathname, depth }),
        []
      )}
    </List>
  );
}

function reduceChildRoutes({
  acc,
  pathname,
  item,
  depth
}) {
  const key = item.title + depth;

  if (item.items) {
    const open = matchPath(pathname, {
      path: item.href,
      exact: false
    });

    acc.push(
      <NavItem
        depth={depth}
        icon={item.icon}
        info={item.info}
        key={key}
        open={Boolean(open)}
        title={item.title}
      >
        {renderNavItems({
          depth: depth + 1,
          pathname,
          items: item.items
        })}
      </NavItem>
    );
  } else {
    acc.push(
      <NavItem
        depth={depth}
        href={item.href}
        icon={item.icon}
        info={item.info}
        key={key}
        title={item.title}
      />
    );
  }

  return acc;
}


const useStyles = makeStyles(theme => ({
  mobileDrawer: {
    width: 256
  },
  desktopDrawer: {
    width: 256,
    top: 64,
    height: 'calc(100% - 64px)'
  },
  avatar: {
    cursor: 'pointer',
    width: 64,
    height: 64
  },
  card: {
    backgroundColor: theme.palette.background.dark
  }
}));

const NavBar = ({ onOpen }) => {
  const classes = useStyles();
  const location = useLocation();
  const acc = useSelector((state) => state.account);


  const content = (
    <Box
      height='100%'
      display='flex'
      flexDirection='column'
    >
      <PerfectScrollbar options={{ suppressScrollX: true }}>
        <Box p={2}>
          <Box
            display='flex'
            justifyContent='center'
          >
            <Avatar className={classes.cityAvatar}>
              <UserIcon size='22' />
            </Avatar>
          </Box>
        </Box>
        <Box disply="flex">
          <Paper elevation={1} >
            <Box className={classes.overview}
              textAlign="center"
            >
              <Typography
                display="inline"
                variant="body1"
                color="secondary"
              >
                <NumberFormat 
                  value={1000} 
                  displayType={'text'} 
                  thousandSeparator={true} 
                />
              </Typography>
            </Box>
            <Divider />
            <Box
              justifyContent='center'
              display='flex'
              mt={2}
            >
              <Button
                size="small"
                color="secondary"
                variant="contained"
                component={RouterLink}
                to="/app/stake"
                fullWidth
              >
                {acc.loggedIn && stakedAmount > 0 ? 'Stake more' : 'Stake'}
              </Button>
            </Box>    
          </Paper>
          <Paper className={classes.paper}
            elevation={1}
          >
            <Box className={classes.overview}
              textAlign="center"
            >
            <Typography
              display="inline"
              variant="body1"
              color="secondary"
            >
              <NumberFormat 
                value={2200} 
                displayType={'text'} 
                thousandSeparator={true} 
              />
            </Typography>
            </Box>
            <Divider />
            <Box
              mt={2}
              display="flex"
              justifyContent='center'
            >
              <Button
                size="small"
                color="secondary"
                variant="contained"
                component={RouterLink}
                to="/app/stakeRewards"
                fullWidth
              >
                Stake
              </Button>
            </Box>
          </Paper>
        </Box>
        <Divider />
        <Box p={2}>
          {mySections.map(section => (
            <List
              key={section.subheader}
              subheader={(
                <ListSubheader
                  disableGutters
                  disableSticky
                >
                  {section.subheader}
                </ListSubheader>
              )}
            >
              {renderNavItems({
                items: section.items,
                pathname: location.pathname
              })}
            </List>
          ))}
        </Box>
        <Divider />
        <Box p={2}>
          {sections.map(section => (
            <List
              key={section.subheader}
              subheader={(
                <ListSubheader
                  disableGutters
                  disableSticky
                >
                  {section.subheader}
                </ListSubheader>
              )}
            >
              {renderNavItems({
                items: section.items,
                pathname: location.pathname
              })}
            </List>
          ))}
        </Box>


        <Divider />
        
        <Box p={2}>
        <Paper elevation={10} >
          <Box
            p={2}
            borderRadius="borderRadius"
            bgcolor="background.dark"
          >
            <Typography
              variant="h6"
              color="textPrimary"
            >
              Need Help?
            </Typography>
            <Link
              variant="subtitle1"
              color="secondary"
              component={RouterLink}
              to="/docs"
            >
              Check our docs
            </Link>
          </Box>
          </Paper>
        </Box>
      </PerfectScrollbar>
    </Box>
  );

  return (
    <Drawer
      anchor="right"
      classes={{ paper: classes.desktopDrawer }}
      open={onOpen}
      variant="persistent"
    >
      {content}
    </Drawer>
  );
};

NavBar.propTypes = {
  onOpen: PropTypes.bool
};

export default NavBar;
