import React, { useEffect, useState, useCallback } from 'react';
import { NavLink as RouterLink } from 'react-router-dom';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import { THEMES } from 'src/constants';
import {
  Avatar,
  Box,
  Button,
  Card,
  CardMedia,
  Container,
  Divider,
  Grid,
  Link,
  Tab,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TablePagination,
  TableRow,
  Tabs,
  Typography,
  Paper,
  useTheme,
  useMediaQuery,
  makeStyles
} from '@material-ui/core';
import purple from '@material-ui/core/colors/purple';

const font = "'Saira', sans-serif";

const challenges = [
  {
    id: 1,
    opponentName: "mukki@chaingames.io",
    opponentAvatar: "/static/images/panda.png",
    game: "Madden NFL 21",
    gameFormat: "Max Score", 
    startTime: "27th Mar 21:00 CET",
    duration: "30 Min",
    betAmount: 5,
    status: "Accepted",
    eventDetails: "Event",
    result: "Won"
  },
  {
    id: 2,
    opponentName: "mukki@chaingames.io",
    opponentAvatar: "/static/images/panda.png",
    game: "Madden NFL 21",
    gameFormat: "Max Score", 
    startTime: "24th Mar 21:00 CET",
    duration: "30 Min",
    betAmount: 25,
    status: "Accepted",
    eventDetails: "Event",
    result: "Won"
  },
  {
    id: 3,
    opponentName: "mukki@chaingames.io",
    opponentAvatar: "/static/images/panda.png",
    game: "Madden NFL 21",
    gameFormat: "Max Score", 
    startTime: "21th Mar 21:00 CET",
    duration: "30 Min",
    betAmount: 1,
    status: "Accepted",
    eventDetails: "Event",
    result: "Lost"
  }
]



const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.background.dark,
    paddingBottom: 200,
    minHeight: '100%',
    [theme.breakpoints.down('md')]: {
      paddingTop: 0,
      paddingBottom: 60
    },
    margin: 0
  },
  resultWon: {
    color: '#388e3c'
  },
  resultLost: {
    color: '#f44336'
  },
  rowImage: {
    height: '48px',
    width: '48px',
    margin: 0,
    padding: 0,
    verticalAlign: 'top'
  },
}));

const Challenges = ({ className, ...rest }) => {
  const classes = useStyles();
  const theme = useTheme();
  const mobileDevice = useMediaQuery(theme.breakpoints.down('sm'));
  const [currentTab, setCurrentTab] = useState('sent');

  const tabs = [
    { value: 'sent', label: 'Sent' },
    { value: 'received', label: 'Received' },
    { value: 'accepted', label: 'Accepted' },
    { value: 'completed', label: 'Completed' }
  ];

  const handleTabsChange = (event, value) => {
    setCurrentTab(value);
  };

  return (
    <div
      className={clsx(classes.root, className)}
      {...rest}
    > 
      <Container maxWidth="lg">
        <Typography
          variant="h1"
          color="textPrimary"
        >
          My Challenges
        </Typography>
        <Box mt={3}>
          <Paper>
            <Box mt={1} mb={3}>
              <Tabs
                onChange={handleTabsChange}
                scrollButtons="auto"
                textColor="secondary"
                value={currentTab}
                variant="scrollable"
              >
                {tabs.map((tab) => (
                  <Tab
                    key={tab.value}
                    label={tab.label}
                    value={tab.value}
                  />
                ))}
              </Tabs>
              <Divider />
            </Box>
            <Card>
              <Box minWidth={300} >
                <Table>
                  <TableHead>
                    <TableRow >
                      <TableCell>
                        Opponent
                      </TableCell>
                      <TableCell>
                        Game
                      </TableCell>
                      <TableCell>
                        Game Format
                      </TableCell>
                      <TableCell>
                        Start Time
                      </TableCell>
                      <TableCell>
                        Duration
                      </TableCell>
                      <TableCell>
                        Bet Amount
                      </TableCell>
                      <TableCell>
                        Status
                      </TableCell>
                      <TableCell>
                        Event Details
                      </TableCell>
                      <TableCell>
                        Result
                      </TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {challenges.map((entry) => {
                      return (
                        <TableRow
                          spacing={0}
                          hover
                          key={entry.id}
                        >
                          <TableCell className={classes.imageCell} align='center' padding='none'>
                              <img className={classes.rowImage}
                                src={entry.opponentAvatar}
                              />
                          </TableCell>
                          <TableCell>
                            {entry.game}
                          </TableCell>
                          <TableCell>
                            {entry.gameFormat}
                          </TableCell>
                          <TableCell>
                            {entry.startTime}
                          </TableCell>
                          <TableCell>
                            {entry.duration}
                          </TableCell>
                          <TableCell>
                            {entry.betAmount}
                          </TableCell>
                          <TableCell>
                            {entry.status}
                          </TableCell>
                          <TableCell>
                            <Link
                              color="secondary"
                              to={entry.eventDetails}
                            >
                              Event
                            </Link>
                          </TableCell>
                          <TableCell>
                            <Typography
                              className={entry.result == 'Won' ? classes.resultWon : classes.resultLost}
                              variant="body2"
                            >
                              {entry.result}
                            </Typography>
                          </TableCell>
                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
                <TablePagination
                  component="div"
                  count={12}
                  labelRowsPerPage={'Rows per page'}
                  /* onChangePage={handlePageChange}
                  onChangeRowsPerPage={handleLimitChange} */
                  page={0}
                  rowsPerPage={10}
                  rowsPerPageOptions={[5, 10, 25]}
                />
              </Box>
            </Card>
          </Paper>
        </Box>
      </Container>
    </div>
  );
};

Challenges.propTypes = {
  className: PropTypes.string
};

export default Challenges;
