import React, { useState } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import {
  Box,
  Card,
  Container,
  Divider,
  Paper,
  Tab,
  Tabs,
  Typography,
  makeStyles
} from '@material-ui/core';
import Page from 'src/components/Page';

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.background.dark,
    minHeight: '100%',
    minWidth: '100%',
    paddingBottom: theme.spacing(3),
  },
  title: {
    position: 'relative',
    '&:before': {
      position: 'absolute',
      bottom: -2,
      left: -16,
      content: '" "',
      height: 40,
      width: 6,
      backgroundColor: theme.palette.secondary.main,
      marginRight: '20px'
    }
  }
}));


const BattleView = () => {
  const classes = useStyles();

  const tabs = [
    { value: 'overview', label: 'Overview' },
    { value: 'prices', label: 'Prices' },
    { value: 'howToPlay', label: 'How to Play' },
    { value: 'rules', label: 'Rules' }
  ];

  return (
    <Page
      className={classes.root}
      title="Battle"
    >
      <Container maxWidth="lg">
        <Box ml={2} mt={5} mb={3}>
          <Typography
            display="inline"
            variant="h2"
            color="textPrimary"
            className={classes.title}
          >
            Battle
          </Typography>
        </Box>
        <Card
          className={classes.card}
        >
            
        </Card>
      </Container>
      <Container maxWidth="lg">
        <Box ml={2} mt={5} mb={3}>
          <Typography
            display="inline"
            variant="h2"
            color="textPrimary"
            className={classes.title}
          >
            Upcoming Events
          </Typography>
        </Box>
        <Box mt={1} mb={3}>
          <Tabs
            onChange={handleTabsChange}
            scrollButtons="auto"
            textColor="secondary"
            value={currentTab}
            variant="scrollable"
          >
            {tabs.map((tab) => (
              <Tab
                key={tab.value}
                label={tab.label}
                value={tab.value}
              />
            ))}
          </Tabs>
          <Divider />
        </Box>
        <Card
          className={classes.card}
        >
          
          
        </Card>
      </Container>
      
    </Page>
  );
};

export default BattleView;
