import React from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import {
  Avatar,
  Box,
  Button,
  Card,
  Container,
  Grid,
  SvgIcon,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TablePagination,
  TableRow,
  Typography,
  useTheme,
  useMediaQuery,
  makeStyles
} from '@material-ui/core';
import ArrowDropDownIcon from '@material-ui/icons/ArrowDropDown';
import { Download as DownloadIcon } from 'react-feather';

const font = "'Saira', sans-serif";

const events = [
  {
    id: "1",
    game: "COD - MW",
    eventName: "Free MW",
    format: "Warzone - Max Kills",
    entry: "Free",
    status: "Waiting",
    result: "Won",
    startTime: "00:00:04:23"
  },
  {
    id: "2",
    game: "COD - MW",
    eventName: "Free MW",
    format: "Warzone - Max Kills",
    entry: "Free",
    status: "Waiting",
    result: "Won",
    startTime: "00:00:05:23"
  },
  {
    id: "3",
    game: "COD - MW",
    eventName: "Free MW",
    format: "Warzone - Max Kills",
    entry: "Free",
    status: "Waiting",
    result: "Lost",
    startTime: "00:01:04:23"
  }
]


const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.background.dark,
    paddingBottom: 200,
    paddingTop: 100,
    minHeight: '100%',
    [theme.breakpoints.down('md')]: {
      paddingTop: 0,
      paddingBottom: 60
    },
    margin: 0
  },
  card: {
    minHeight: "200px",
    padding: theme.spacing(4)
  },
  avatar: {
    cursor: 'pointer',
    width: 64,
    height: 64
  },
  rowImage: {
    height: '48px',
    width: '48px',
    margin: 0,
    padding: 0,
    verticalAlign: 'top'
  },
  imageCell: {
    height: '48px',
    width: '48px',
    padding: 0
  },
  accordion: {
    marginTop: theme.spacing(4)
  },
  title: {
    fontFamily: font,
    fontSize: 32
  },
  userName: {
    fontFamily: font,
    fontSize: 24
  },
  statusesButton: {
    marginTop: theme.spacing(2)
  }
}));

const Profile = ({ className, ...rest }) => {
  const classes = useStyles();
  const theme = useTheme();
  const mobileDevice = useMediaQuery(theme.breakpoints.down('sm'));

  return (
    <div
      className={clsx(classes.root, className)}
      {...rest}
    > 
      <Container maxWidth="lg">
        <Grid container spacing={2}>
          <Grid item xs={12} lg={4}>
            <Card className={classes.card}>
              <Box
                display='flex'
                justifyContent='center'
              >
                <Avatar className={classes.avatar} src="/static/images/panda.png" />
              </Box>
              <Box 
                marginTop={2}
              >
                <Box 
                  display='flex'
                  justifyContent='center'
                  marginTop={1}
                >
                  <Typography
                    className={classes.userName}
                    variant="body2"
                    color="textPrimary"
                  >
                    MUKKI
                  </Typography>
                </Box>
                <Box mt={4}>
                  <Button
                    className={classes.button}
                    size="large"
                    color="secondary"
                    variant="text"
                    startIcon={
                      <SvgIcon fontSize="small">
                        <DownloadIcon />
                      </SvgIcon>
                    }
                  >
                    Matches
                  </Button>
                </Box>
                <Box mt={2}>
                  <Button
                    className={classes.button}
                    size="large"
                    color="secondary"
                    variant="text"
                    fullWidth
                    startIcon={
                      <SvgIcon fontSize="small">
                        <DownloadIcon />
                      </SvgIcon>
                    }
                  >
                    Gaming Networks
                  </Button>
                </Box>
                <Box mt={2}>
                  <Button
                    className={classes.button}
                    size="large"
                    color="secondary"
                    variant="text"
                    fullWidth
                    startIcon={
                      <SvgIcon fontSize="small">
                        <DownloadIcon />
                      </SvgIcon>
                    }
                  >
                    Followers
                  </Button>
                </Box>
                <Box mt={2}>
                  <Button
                    className={classes.button}
                    size="large"
                    color="secondary"
                    variant="text"
                    fullWidth
                    startIcon={
                      <SvgIcon fontSize="small">
                        <DownloadIcon />
                      </SvgIcon>
                    }
                  >
                    Following
                  </Button>
                </Box>
              </Box>
            </Card>
          </Grid>
          <Grid item xs={12} lg={8}>
            <Card className={classes.card}>
              <Box minWidth={300} >
                <Typography
                  className={classes.title}
                  variant="h2"
                  color="sextPrimary"
                >
                  Match History
                </Typography>
                <Button
                  className={classes.statusesButton}
                  aria-controls="menu-shooter"
                  aria-haspopup="true"
                  color="secondary"
                  variant="outlined"
                  /* onClick={handleMenuShooter} */
                >
                  All Statuses
                  <ArrowDropDownIcon />
                </Button>
                <Table>
                  <TableHead>
                    <TableRow >
                      <TableCell>
                        Game
                      </TableCell>
                      <TableCell>
                        Event Name
                      </TableCell>
                      <TableCell>
                        Game Format
                      </TableCell>
                      <TableCell>
                        Entry
                      </TableCell>
                      <TableCell>
                        Status
                      </TableCell>
                      <TableCell>
                        Start Time
                      </TableCell>
                      <TableCell>
                        Result
                      </TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {events.map((entry) => {
                      return (
                        <TableRow
                          spacing={0}
                          hover
                          key={entry.id}
                        >
                          <TableCell>
                            {entry.game}
                          </TableCell>
                          <TableCell>
                            {entry.eventName}
                          </TableCell>
                          <TableCell>
                            {entry.format}
                          </TableCell>
                          <TableCell>
                            <Typography
                              color={entry.entry == 'Free' && 'secondary'}
                              variant="body2"
                            >
                              {entry.entry}
                            </Typography>
                          </TableCell>
                          <TableCell>
                            <Typography
                              className={entry.status == 'Waiting' && classes.waiting}
                              variant="body2"
                            >
                              {entry.status}
                            </Typography>
                          </TableCell>
                          <TableCell>
                            {entry.startTime}
                          </TableCell>
                          <TableCell>
                            {entry.result}
                          </TableCell>
                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
                <TablePagination
                  component="div"
                  count={3}
                  labelRowsPerPage={'Rows per page'}
                  /* onChangePage={handlePageChange}
                  onChangeRowsPerPage={handleLimitChange} */
                  page={0}
                  rowsPerPage={10}
                  rowsPerPageOptions={[5, 10, 25]}
                />
              </Box>
            </Card>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
};

Profile.propTypes = {
  className: PropTypes.string
};

export default Profile;
