import { useLocation } from 'react-router-dom';
import COD_Image from '/static/images/cod_banner.jpg';
import Madden_Image from '/static/images/Madden_Background.jpg'; 
import Fifa_Image from '/static/images/fifa-background-image.jpg'


const GameSectionOne = () => {
  const location = useLocation();

  const getImage = () => {
    switch (location.pathname) {
      case '/actionGamePage/madden2021':
        return Madden_Image;
      case '/actionGamePage/cod':
        return COD_Image;
      case '/actionGamePage/fifa':
        return Fifa_Image;
    }
  }
  return (
    <Paper className={classes.paper} >
      <img
        alt="Presentation"
        src={ getImage()}
      />      
    </Paper>
  );
};

export default GameSectionOne;
