import React, { useEffect, useState, useCallback } from 'react';
import { NavLink as RouterLink } from 'react-router-dom';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import { THEMES } from 'src/constants';
import {
  Box,
  Button,
  Card,
  CardMedia,
  Container,
  Grid,
  Typography,
  useTheme,
  useMediaQuery,
  makeStyles
} from '@material-ui/core';
import useIsMountedRef from 'src/hooks/useIsMountedRef';
import { axios } from 'src/utils/axiosHook';
import { useDispatch, useSelector } from 'src/store';
import Carousel from 'react-material-ui-carousel'

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.background.dark,
    paddingTop: 200,
    paddingBottom: 200,
    minHeight: '100%',
    [theme.breakpoints.down('md')]: {
      paddingTop: 60,
      paddingBottom: 60
    }
  },
  button: {
    color: theme.palette.text.secondary,
    padding: '10px 8px',
    justifyContent: 'flex-start',
    textTransform: 'none',
    letterSpacing: 0,
    width: '100%'
  },
  card: {
    width: '100%',
    height: 320,
    backgroundColor: '#00008B',
    color: '#fff',
    margin: '0 15px'
  },
  image: {
    height: 320,
    backgroundColor: theme.palette.background.dark
  }
}));

const displayCount = 3

const Hero = ({ className, ...rest }) => {
  const classes = useStyles();
  const theme = useTheme();
  const mobileDevice = useMediaQuery(theme.breakpoints.down('sm'));

  return (
    <div
      className={clsx(classes.root, className)}
      {...rest}
    >
      <Container maxWidth="lg">
        <Grid
          container
          spacing={3}
        >
          <Carousel
            infiniteLoop
            centerMode
            centerSlidePercentage={100 / displayCount}
            showStatus={false}
          >
            <Card className={classes.card}>
              <CardMedia
                className={classes.image}
                component="img"
                image="static/images/panda.png"
              />
            </Card>
            <Card className={classes.card}>
              <CardMedia
                className={classes.image}
                component="img"
                image="static/images/superCryptoKart.jpg"
              />
            </Card>
            <Card className={classes.card}>
              <CardMedia
                className={classes.image}
                component="img"
                image="static/images/nba.png"
              />
            </Card>
            <Card className={classes.card}>
              <CardMedia
                className={classes.image}
                component="img"
                image="static/images/madden.jpg"
              />
            </Card>
            <Card className={classes.card}>
              <CardMedia
                className={classes.image}
                component="img"
                image="static/images/fortnite.png"
              />
            </Card>
            <Card className={classes.card}>
              <CardMedia
                className={classes.image}
                component="img"
                image="static/images/fifa21.jpg"
              />
            </Card>
            <Card className={classes.card}>
              <CardMedia
                className={classes.image}
                component="img"
                image="static/images/fallGuys.jpg"
              />
            </Card>
            <Card className={classes.card}>
              <CardMedia
                className={classes.image}
                component="img"
                image="static/images/apex.jpg"
              />
            </Card>
          </Carousel>
        </Grid>
      </Container>
    </div>
  );
};

Hero.propTypes = {
  className: PropTypes.string
};

export default Hero;
