import React, { useState, useEffect, useRef, useContext } from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import { useLocation, matchPath } from 'react-router-dom';
import {
  Avatar,
  Box,
  Button,
  Card,
  Container,
  Divider,
  Grid,
  SvgIcon,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TablePagination,
  TableRow,
  Typography,
  useTheme,
  useMediaQuery,
  makeStyles
} from '@material-ui/core';
import ArrowDropDownIcon from '@material-ui/icons/ArrowDropDown';
import { Download as DownloadIcon } from 'react-feather';
import { Monitor as MatchesIcon } from 'react-feather';
import { Link2 as NetworkIcon } from 'react-feather';
import { Users as FollowersIcon } from 'react-feather';
import { UserCheck as FollowingIcon } from 'react-feather';

// new
import defaultAvatar from "../../assets/img/placeholder.jpg";
import {
  userInfoService,
  followService,
  unFollowService,
  checkIsFollowingService,
  getBalanceFromCS,
} from '../../service/node.service'
import { AuthContext } from "../../context/AuthContext";
//import MatchHistory from "./MatchHistory";
//import Followers from "./Followers";
//import Following from "./Following";
//import GamingNetworks from './GamingNetworks';
//import ChallengeModal from '../challenges/ChallengeModal';
//import ImageTagWithErrorImage from '../ImageConponentWithDefaultAvatar/index';
import {
  getFormattedUserName,
  formatInCHAIN,
} from '../../utils/helpers.js';
//import { useRouter } from "next/router";
import * as Sentry from "@sentry/react";

const font = "'Saira', sans-serif";

const events = [
  {
    id: "1",
    game: "COD - MW",
    eventName: "Free MW",
    format: "Warzone - Max Kills",
    entry: "Free",
    status: "Waiting",
    result: "Won",
    startTime: "00:00:04:23"
  },
  {
    id: "2",
    game: "COD - MW",
    eventName: "Free MW",
    format: "Warzone - Max Kills",
    entry: "Free",
    status: "Waiting",
    result: "Won",
    startTime: "00:00:05:23"
  },
  {
    id: "3",
    game: "COD - MW",
    eventName: "Free MW",
    format: "Warzone - Max Kills",
    entry: "Free",
    status: "Waiting",
    result: "Lost",
    startTime: "00:01:04:23"
  }
]


const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.background.dark,
    paddingBottom: 200,
    paddingTop: 100,
    minHeight: '100%',
    [theme.breakpoints.down('md')]: {
      paddingTop: 0,
      paddingBottom: 60
    },
    margin: 0
  },
  card: {
    minHeight: "200px",
    padding: theme.spacing(4)
  },
  avatar: {
    cursor: 'pointer',
    width: 64,
    height: 64
  },
  rowImage: {
    height: '48px',
    width: '48px',
    margin: 0,
    padding: 0,
    verticalAlign: 'top'
  },
  imageCell: {
    height: '48px',
    width: '48px',
    padding: 0
  },
  accordion: {
    marginTop: theme.spacing(4)
  },
  title: {
    fontFamily: font,
    fontSize: 32
  },
  userName: {
    fontFamily: font,
    fontSize: 24
  },
  statusesButton: {
    marginTop: theme.spacing(2)
  },
  button: {
    marginLeft: theme.spacing(2),
    marginBottom: theme.spacing(1),
    marginTop: theme.spacing(1),
  },
  waiting: {
    color: '#388e3c'
  }
}));

const applyPagination = (list, page, limit) => {
  return list.slice(page * limit, page * limit + limit);
};

const Profile = ({ className, ...rest }) => {
  const classes = useStyles();
  const location = useLocation();
  const [page, setPage] = useState(0);
  const [limit, setLimit] = useState(10);

  // new
  const {user, dispatch} = useContext(AuthContext)
  //const router = useRouter()
  //const { username } = router.query
  const { username } = "mukki"
  const wrapper = useRef('wrapper');
  //const [profileTabs, setProfileTabs] = useState(TabsEnum.MatchHistory);
  //const [imageURL, setImageURL] = useState(defaultAvatar);
  //const [name, setName] = useState('');
  //const [isFollowing, setIsFollowing] = useState(false);
  const [isBanned, setIsBanned] = useState(false);
  const [isOwnProfile, setIsOwnProfile] = useState(true);
  //const [showChallengeModal, setShowChallengeModal] = useState(false);
  const [chainNetworkBalance, setChainNetworkBalance] = useState(0);

  const handlePageChange = (event, newPage) => {
    setPage(newPage);
  };

  const handleLimitChange = (event) => {
    setLimit(parseInt(event.target.value));
  };

  const paginatedEvents = applyPagination(events, page, limit);

  useEffect(()=>{
    setIsOwnProfile(user.user?.session?.username === username);
    console.log(user)
  },[user, username])

  async function getProfileInfo(){
    try{
      const [ userInfo, checkFollowing, balanceInfo] = await Promise.all([
        userInfoService({ username }),
        checkIsFollowingService({ username }),
        getBalanceFromCS({}),
      ])
      
      if(checkFollowing?.data?.success === true){
        setIsFollowing(checkFollowing.data.isFollowing)
      }

      if (balanceInfo.data.success) {
        const networkFormatInChain = formatInCHAIN(balanceInfo.data.token.total);
        setChainNetworkBalance(networkFormatInChain);
      }

        if(userInfo.data.success === true){
          setName(userInfo?.data?.username);
          setIsBanned(userInfo?.data?.isBanned);
          setImageURL(userInfo?.data?.dpHigh ? (userInfo?.data?.dpHigh) : defaultAvatar);
        }
    }
    catch(error){
      console.log("ðŸš€ ~ file: ProfilePageSkeleton.jsx ~ line 89 ~ getProfileInfo ~ error", error)
      Sentry.captureException(error, {
        tags: {
          page: location.pathname,
        },
      });
      if (error?.response?.data?.error === 'user not found') {
        router.push('/404');
      }
    }
  }

  useEffect(()=>{
    getProfileInfo();
  },[username])

  return (
    <div
      className={clsx(classes.root, className)}
      {...rest}
    > 
      <Container maxWidth="lg">
        <Grid container spacing={2}>
          <Grid item xs={12} lg={4}>
            <Card className={classes.card}>
              <Box
                display='flex'
                justifyContent='center'
              >
                <Avatar className={classes.avatar} src="/static/images/panda.png" />
              </Box>
              <Box 
                marginTop={2}
              >
                <Box 
                  display='flex'
                  justifyContent='center'
                  marginTop={1}
                >
                  <Typography
                    className={classes.userName}
                    variant="body2"
                    color="textPrimary"
                  >
                    {getFormattedUserName(name?.toUpperCase(), 9)}
                  </Typography>
                </Box>
                <Box mt={4} mb={1}>
                  <Button
                    className={classes.button}
                    size="large"
                    variant="text"
                    startIcon={
                      <SvgIcon fontSize="small">
                        <MatchesIcon />
                      </SvgIcon>
                    }
                  >
                    Matches
                  </Button>
                </Box>
                <Divider />
                <Box>
                  <Button
                    className={classes.button}
                    size="large"
                    variant="text"
                    startIcon={
                      <SvgIcon fontSize="small">
                        <NetworkIcon />
                      </SvgIcon>
                    }
                  >
                    Gaming Networks
                  </Button>
                </Box>
                <Divider />
                <Box>
                  <Button
                    className={classes.button}
                    size="large"
                    variant="text"
                    startIcon={
                      <SvgIcon fontSize="small">
                        <FollowersIcon />
                      </SvgIcon>
                    }
                  >
                    Followers
                  </Button>
                </Box>
                <Divider />
                <Box>
                  <Button
                    className={classes.button}
                    size="large"
                    variant="text"
                    startIcon={
                      <SvgIcon fontSize="small">
                        <FollowingIcon/>
                      </SvgIcon>
                    }
                  >
                    Following
                  </Button>
                </Box>
              </Box>
            </Card>
          </Grid>
          <Grid item xs={12} lg={8}>
            <Card className={classes.card}>
              <Box minWidth={300} >
                <Typography
                  className={classes.title}
                  variant="h2"
                  color="sextPrimary"
                >
                  Match History
                </Typography>
                <Button
                  className={classes.statusesButton}
                  aria-controls="menu-shooter"
                  aria-haspopup="true"
                  color="secondary"
                  variant="outlined"
                  /* onClick={handleMenuShooter} */
                >
                  All Statuses
                  <ArrowDropDownIcon />
                </Button>
                <Table>
                  <TableHead>
                    <TableRow >
                      <TableCell>
                        Game
                      </TableCell>
                      <TableCell>
                        Event Name
                      </TableCell>
                      <TableCell>
                        Game Format
                      </TableCell>
                      <TableCell>
                        Entry
                      </TableCell>
                      <TableCell>
                        Status
                      </TableCell>
                      <TableCell>
                        Start Time
                      </TableCell>
                      <TableCell>
                        Result
                      </TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {paginatedEvents.map((entry) => {
                      return (
                        <TableRow
                          spacing={0}
                          hover
                          key={entry.id}
                        >
                          <TableCell>
                            {entry.game}
                          </TableCell>
                          <TableCell>
                            {entry.eventName}
                          </TableCell>
                          <TableCell>
                            {entry.format}
                          </TableCell>
                          <TableCell>
                            <Typography
                              color={entry.entry == 'Free' && 'secondary'}
                              variant="body2"
                            >
                              {entry.entry}
                            </Typography>
                          </TableCell>
                          <TableCell>
                            <Typography
                              className={entry.status == 'Waiting' && classes.waiting}
                              variant="body2"
                            >
                              {entry.status}
                            </Typography>
                          </TableCell>
                          <TableCell>
                            {entry.startTime}
                          </TableCell>
                          <TableCell>
                            {entry.result}
                          </TableCell>
                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
                <TablePagination
                  component="div"
                  count={events.length}
                  labelRowsPerPage={'Rows per page'}
                  onChangePage={handlePageChange}
                  onChangeRowsPerPage={handleLimitChange}
                  page={page}
                  rowsPerPage={limit}
                  rowsPerPageOptions={[5, 10, 25]}
                />
              </Box>
            </Card>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
};

Profile.propTypes = {
  className: PropTypes.string
};

export default Profile;
